SELECT FBELONGMONTH, SUM( CASE COMPANY WHEN '02' THEN RATE END ) AS COMPANY2, SUM( CASE COMPANY WHEN '06' THEN RATE END ) AS COMPANY6, SUM( CASE COMPANY WHEN '07' THEN RATE END ) AS COMPANY7, SUM( CASE COMPANY WHEN '09' THEN RATE END ) AS COMPANY9 FROM ( SELECT DISTINCT COMPANY, FBELONGMONTH, HEGE, TOTAL, HEGE / TOTAL * 100 AS RATE FROM ( SELECT DISTINCT COMPANY, CONCAT( YEAR ( FCOUNTTIMEDTM ), "-", MONTH ( FCOUNTTIMEDTM ) ) AS FBELONGMONTH, STATE, SUM( CASE WHEN STATE = '合格' THEN 1 ELSE 0 END ) HEGE, SUM( CASE WHEN STATE = '合格' OR STATE = '不合格' THEN 1 ELSE 0 END ) TOTAL FROM ( SELECT COMPANY, FCOUNTTIMEDTM, IF ( ( RATE1 BETWEEN - 2 AND 2 ) AND ( RATE2 BETWEEN - 2 AND 2 ) AND ( RATE3 BETWEEN - 3 AND 3 ), "合格", "不合格" ) AS STATE FROM ( SELECT *, CASE COMPANY WHEN '02' THEN ( ( RTON - BQTON ) / BQTON * 100 ) WHEN '06' THEN ( ( RTON - BQTON ) / BQTON * 100 ) WHEN '07' THEN ( ( RTON - BMTON ) / BMTON * 100 ) WHEN '09' THEN ( ( RTON - BMTON ) / BMTON * 100 ) END AS RATE1, CASE COMPANY WHEN '02' THEN ( ( RTON - BMTON ) / BMTON * 100 ) WHEN '06' THEN ( ( RTON - BJTON ) / BJTON * 100 ) WHEN '07' THEN ( ( RTON - BMTON ) / BMTON * 100 ) WHEN '09' THEN ( ( RTON - BQTON ) / BQTON * 100 ) END AS RATE2, CASE COMPANY WHEN '02' THEN ( ( BQTON - BMTON ) / ( BQTON + BMTON ) / 2 * 100 ) WHEN '06' THEN ( ( BQTON - BJTON ) / ( BQTON + BJTON ) / 2 * 100 ) WHEN '07' THEN ( ( BJTON - BMTON ) / ( BJTON + BMTON ) / 2 * 100 ) WHEN '09' THEN ( ( BMTON - BQTON ) / ( BMTON + BQTON ) / 2 * 100 ) END AS RATE3 FROM T_V_SHIPMAIN where 1=1
and FCOUNTTIMEDTM between #{dt_start} and #{dt_end}
) AS PANGSHEN ) AS AAA GROUP BY COMPANY, FBELONGMONTH ) AS BBB ) AS CCC GROUP BY FBELONGMONTH

SELECT DISTINCT COMPANY,FBELONGMONTH,HEGE,TOTAL,HEGE/TOTAL*100 AS RATE FROM( SELECT DISTINCT COMPANY,CONCAT(YEAR(FCOUNTTIMEDTM),"-",MONTH(FCOUNTTIMEDTM)) AS FBELONGMONTH,STATE, SUM(CASE WHEN STATE='合格' THEN 1 ELSE 0 END )HEGE, SUM(CASE WHEN STATE='合格' OR STATE='不合格' THEN 1 ELSE 0 END )TOTAL FROM(SELECT COMPANY,FCOUNTTIMEDTM, IF (( RATE2 BETWEEN - 2 AND 2 ) AND ( RATE6 BETWEEN - 2 AND 2 ) AND ( RATE7 BETWEEN - 3 AND 3 ), "合格", "不合格" ) AS STATE
FROM ( SELECT *, CASE COMPANY WHEN '02' THEN (( RTON - BQTON )/ BQTON * 100 ) WHEN '06' THEN (( RTON - BQTON )/ BQTON * 100 ) WHEN '07' THEN (( RTON - BMTON )/ BMTON * 100 ) WHEN '09' THEN (( RTON - BMTON )/ BMTON * 100 ) END AS RATE2, CASE COMPANY WHEN '02' THEN (( RTON - BMTON )/ BMTON * 100 ) WHEN '06' THEN (( RTON - BJTON )/ BJTON * 100 ) WHEN '07' THEN (( RTON - BMTON )/ BMTON * 100 ) WHEN '09' THEN (( RTON - BQTON )/ BQTON * 100 ) END AS RATE6, CASE COMPANY WHEN '02' THEN (( BQTON - BMTON )/ ( BQTON + BMTON )/ 2 * 100 ) WHEN '06' THEN (( BQTON - BJTON )/ ( BQTON + BJTON )/ 2 * 100 ) WHEN '07' THEN (( BJTON - BMTON )/ ( BJTON + BMTON )/ 2 * 100 ) WHEN '09' THEN (( BMTON - BQTON )/ ( BMTON + BQTON )/ 2 * 100 ) END AS RATE7 FROM T_V_SHIPMAIN WHERE 1=1
AND COMPANY = #{str_company}
AND FCOUNTTIMEDTM between #{dt_start} and #{dt_end}
) AS PANGSHEN)AS AAA group by FBELONGMONTH)AS BBB

SELECT *, IF (( RATE1 BETWEEN - 2 AND 2 ) AND ( RATE2 BETWEEN - 2 AND 2 ) AND ( RATE3 BETWEEN - 3 AND 3 ), "合格", "不合格" ) AS STATE FROM ( SELECT *, CASE COMPANY WHEN '02' THEN (( RTON - BQTON )/ BQTON * 100 ) WHEN '06' THEN (( RTON - BQTON )/ BQTON * 100 ) WHEN '07' THEN (( RTON - BMTON )/ BMTON * 100 ) WHEN '09' THEN (( RTON - BMTON )/ BMTON * 100 ) END AS RATE1, CASE COMPANY WHEN '02' THEN (( RTON - BMTON )/ BMTON * 100 ) WHEN '06' THEN (( RTON - BJTON )/ BJTON * 100 ) WHEN '07' THEN (( RTON - BMTON )/ BMTON * 100 ) WHEN '09' THEN (( RTON - BQTON )/ BQTON * 100 ) END AS RATE2, CASE COMPANY WHEN '02' THEN (( BQTON - BMTON )/ ( BQTON + BMTON )/ 2 * 100 ) WHEN '06' THEN (( BQTON - BJTON )/ ( BQTON + BJTON )/ 2 * 100 ) WHEN '07' THEN (( BJTON - BMTON )/ ( BJTON + BMTON )/ 2 * 100 ) WHEN '09' THEN (( BMTON - BQTON )/ ( BMTON + BQTON )/ 2 * 100 ) END AS RATE3 from T_V_SHIPMAIN WHERE 1=1
AND COMPANY = #{str_company}
AND FCOUNTTIMEDTM BETWEEN #{dt_start} AND #{dt_end}
)AS PANGSHEN
