mapper.select:
select *,FWEIGHTTONNUM-FCHECKTONNUM as PROFITNUM, CAST(ROUND((FWEIGHTTONNUM-FCHECKTONNUM)/FCHECKTONNUM*100,2) AS DECIMAL(38,2)) AS DEVIARATE from T_V_TRAIN
where 1=1

  AND COMPANY = #{str_company}


  AND FBELONGDATETIMEDTM between  #{dt_start} and #{dt_end}

SELECT COMPANY,FBELONGMONTH,WEIGHTTRAIN,NOWEIGHTTRAIN,TOTALTRAIN,WEIGHTTRAIN/TOTALTRAIN*100 AS RATE FROM( SELECT DISTINCT COMPANY, CONCAT(YEAR(FBELONGDATETIMEDTM),"-",MONTH(FBELONGDATETIMEDTM)) AS FBELONGMONTH, SUM(CASE WHEN ISVALID=1 THEN 1 ELSE 0 END )WEIGHTTRAIN, SUM(CASE WHEN ISVALID=0 THEN 1 ELSE 0 END )NOWEIGHTTRAIN, SUM(CASE WHEN ISVALID=0 OR ISVALID=1 THEN 1 ELSE 0 END )TOTALTRAIN from T_V_TRAIN where 1=1
AND FBELONGDATETIMEDTM between #{dt_start} and #{dt_end}
GROUP BY COMPANY,FBELONGMONTH)AS AA

select FTRAINCODEVCR, CAST(ROUND((FWEIGHTTONNUM-FCHECKTONNUM)/FCHECKTONNUM,2) AS DECIMAL(38,2)) AS RATE from T_V_TRAIN
where 1=1

  AND COMPANY = #{str_company}


  AND FBELONGDATETIMEDTM between  #{dt_start} and #{dt_end}

select.if:
select * from T_V_TRAIN
where 1=1

  AND FTRAINCODEVCR = #{str_ftraincodevcr}


  AND COMPANY = #{str_company}


  AND FBELONGDATETIMEDTM between  #{dt_start} and #{dt_end}

mapper.select:
select distinct COMPANY,FTRAINCODEVCR from T_V_TRAIN
where 1=1

  AND FTRAINCODEVCR = #{str_ftraincodevcr}


  AND COMPANY = #{str_company}


  AND FBELONGDATETIMEDTM between  #{dt_start} and #{dt_end}

select.if:
SELECT *, CAST(ROUND((SELECT AVG(FEMPTYTONNUM) FROM T_V_TRAIN ORDER BY FEMPTYTONNUM LIMIT 0,3),2) AS DECIMAL(38,2)) AS LIMITEMPTY FROM ( SELECT *, CAST(ROUND((FVOLUME - LIMITVOL) / LIMITVOL * 100,2) AS DECIMAL(38,2)) AS VOLRATE FROM ( SELECT *, (select MIN(FVOLUME) from T_V_TRAIN WHERE FTRAINCODEVCR = #{str_trainno} AND COMPANY = #{str_company} ) AS LIMITVOL, CAST(ROUND((FWEIGHTTONNUM - FCHECKTONNUM) / FCHECKTONNUM * 100,2) AS DECIMAL(38,2)) AS WEIGHTRATE FROM T_V_TRAIN WHERE 1=1
AND COMPANY = #{str_company}
AND FTRAINCODEVCR = #{str_trainno}
AND FBELONGDATETIMEDTM between #{dt_start} and #{dt_end}
) AS PANGSHEN1 ) AS PANGSHEN2

mapper.select:
SELECT COMPANY, MONTH ( FBELONGDATETIMEDTM ) AS FBELONGMM, SUM( FEMPTYTONNUM ) AS FEMPTYTONNUM, MIN( FEMPTYTONNUM ) * COUNT( * ) AS AVGNUM, CAST(ROUND(MIN( FEMPTYTONNUM ) * COUNT( * ) / SUM( FEMPTYTONNUM ) * 100,2) AS DECIMAL(38,2)) AS RATE FROM T_V_TRAIN WHERE 1=1
AND COMPANY = #{str_company}
AND YEAR ( FBELONGDATETIMEDTM ) = #{dt_year}
GROUP BY MONTH (FBELONGDATETIMEDTM),COMPANY

select *,CAST(ROUND((FWEIGHTTONNUM-FCHECKTONNUM)/FCHECKTONNUM*100,2) AS DECIMAL(38,2)) AS RATE from T_V_TRAIN
where 1=1

  AND COMPANY = #{str_company}


  AND FBELONGDATETIMEDTM between  #{dt_start} and #{dt_end}


  AND FSTATSTASTIONNAME = #{str_station}


  AND FCOALNAME = #{str_coalname}

select * from T_V_TRAIN
where 1=1

  AND COMPANY = #{str_company}


  AND FTRAINCODEVCR = #{str_trainno}


  AND ISVALID = #{str_status}


  AND FBELONGDATETIMEDTM between  #{dt_start} and #{dt_end}

SELECT *, CAST(ROUND(AVG( RATE ),2) AS DECIMAL(38,2)) AS RATE FROM ( SELECT *, MONTH( FBELONGDATETIMEDTM ) AS FBELONGMM, CAST(ROUND(( FWEIGHTTONNUM - FCHECKTONNUM ) / FCHECKTONNUM * 100,2) AS DECIMAL(38,2)) AS RATE FROM T_V_TRAIN WHERE 1=1
AND COMPANY = #{str_company}
AND YEAR ( FBELONGDATETIMEDTM ) = #{dt_year}
AND FSTATSTASTIONNAME = #{str_station}
AND FCOALNAME = #{str_coalname}
) AS AAA GROUP BY FCOALNAME,FBELONGMM
